{
    "policy_name": "Annual Leave Policy 2024",
    "policy_type": "Annual",
    "description": "Standard annual leave policy with 15 days entitlement",
    "location": "New York",
    "applies_to": ["Developer", "Manager", "Sales"],
    "excludes": ["Intern", "Contractor"],
    "entitlement": {
      "base_days": 15,
      "employment_types": ["Permanent"]
    },
    "carry_forward": 5,
    "carry_forward_priority": true,
    "encashment": 5,
    "encashment_priority": false,
    "calculation_base": "PER_DAY_SALARY",
    "multiplier": 1.0,
    "notice_period": 7,
    "limit_per_month": 0,
    "reset_leave_counter": "BEGINNING_OF_YEAR",
    "employment_duration": "6_MONTHS",
    "employment_duration_custom_days": 0,
    "can_apply_previous_date": false,
    "document_required": false,
    "allow_multiple_day": true,
    "allow_half_day": true,
    "allow_comment": true,
    "request_on_notice_period": false,
    "approval_route": ["HR", "CHRO"],
    "conditions": [
      {
        "id": "cond_annual_balance_check",
        "description": "Check if requested days <= balance",
        "checks": [
          {"field": "leave_type", "operator": "eq", "value": "Annual"},
          {"field": "leave_days", "operator": "lte", "value": "{accrual_balance}"}
        ],
        "action_if_true": {
          "type": "allow_request",
          "params": {}
        }
      },
      {
        "id": "cond_annual_exceed_balance",
        "description": "Reject if exceeds balance",
        "checks": [
          {"field": "leave_type", "operator": "eq", "value": "Annual"},
          {"field": "leave_days", "operator": "gt", "value": "{accrual_balance}"}
        ],
        "action_if_true": {
          "type": "reject_request",
          "params": {"reason": "Insufficient balance"}
        }
      },
      {
        "id": "cond_probation_restrict",
        "description": "No Annual leave during probation",
        "checks": [
          {"field": "employment_type", "operator": "eq", "value": "Probation"}
        ],
        "action_if_true": {
          "type": "reject_request",
          "params": {"reason": "Not allowed during probation period"}
        }
      }
    ]
  }

